    private Utils(Context context) {

        //we need to use context that is why we used it as a parameter as a constructor.
        sharedPreferences = context.getSharedPreferences("alternate db",Context.MODE_PRIVATE);

        if(null == getAll_Books()){
            //all_Books = new ArrayList<>();
            initData();
        }

        SharedPreferences.Editor editor = sharedPreferences.edit();
        Gson gson = new Gson();

        if(null == getAlready_Read_Books()){

            editor.putString(ALREDADY_READ_BOOKS, gson.toJson(new ArrayList<Books>()));
            editor.commit();

            //this is how we are initializing ALREDADY_READ_BOOKS as an empty arraylist to our sharedPreferences.

        }

        if(null == getWant_to_read_books()){
            editor.putString(WANT_TO_READ_BOOKS, gson.toJson(new ArrayList<Books>()));
            editor.commit();
        }

        if(null == getCurrently_read_books()){
            editor.putString(CURRENTLY_READING_BOOKS, gson.toJson(new ArrayList<Books>()));
            editor.commit();
        }

        if(null == getFavourite_books()){
            editor.putString(FAVOURITE_BOOKS, gson.toJson(new ArrayList<Books>()));
            editor.commit();
        }
    }

