    //this is a singleton
    public static Utils getInstance(Context context) {

        if(null != instance)
            return instance;

        else{
            instance = new Utils(context);
            return instance;
        }
    }

    public ArrayList<Books> getAll_Books() {
        Gson gson = new Gson();

        Type type = new TypeToken < ArrayList<Books> > (){}.getType();

        ArrayList<Books> books = gson.fromJson(sharedPreferences.getString(ALL_BOOKS_KEY, null), type );   //here SharedPreferences can't be used bcz it's a nonStatic field so we will remve static keyword from the method
        return books;
    }

