    public Books getBookById(int id){

        ArrayList<Books> books = getAll_Books();

        if(null != books){
            for (Books b : books){
                if(b.getId() == id){
                    return b;
                }
            }
        }

        return null;

    }

    public boolean addToAlreadyRead (Books book){
        //return already_Read_Books.add(book);        // it's indicating mainly if a book has been added successfully

        ArrayList<Books > books = getAlready_Read_Books();

        if(null != books){
            if( books.add(book)){
                Gson gson = new Gson();
                SharedPreferences.Editor editor = sharedPreferences.edit();
                editor.remove(ALREDADY_READ_BOOKS);
                editor.putString(ALREDADY_READ_BOOKS, gson.toJson(books));
                editor.commit();

                return true;

            }
        }
        return false;
    }

